  <img title="Phong cách gây bão của Phạm Băng Băng tại Cannes, có váy của NTK Việt - 5" src="data:image/svg+xml;charset=utf-8,%3Csvg xmlns%3D''http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg'' viewBox%3D''0 0 1024 1536''%3E%3Crect x=''0'' y=''0'' width=''100%'' height=''100%'' style=''fill:rgb(241, 245, 249)'' %2F%3E%3C%2Fsvg%3E" alt="Phong cách gây bão của Phạm Băng Băng tại Cannes, có váy của NTK Việt - 5" data-width="1024" data-height="1536" data-original="https://icdn.dantri.com.vn/2023/05/18/pham-bang-bang-7-1684375773032.jpg" data-photo-id="2469174" data-track-content="" data-content-name="article-content-image" data-content-piece="article-content-image_2469174" data-content-target="/giai-tri/phong-cach-gay-bao-cua-pham-bang-bang-tai-cannes-co-vay-cua-ntk-viet-20230520085618510.htm" data-src="https://icdn.dantri.com.vn/thumb_w/680/2023/05/18/pham-bang-bang-7-1684375773032.jpg" data-srcset="https://icdn.dantri.com.vn/thumb_w/680/2023/05/18/pham-bang-bang-7-1684375773032.jpg 1x, https://icdn.dantri.com.vn/thumb_w/1020/2023/05/18/pham-bang-bang-7-1684375773032.jpg 1.5x, https://icdn.dantri.com.vn/thumb_w/1360/2023/05/18/pham-bang-bang-7-1684375773032.jpg 2x">
