  <img title="Ngày cuối điều chỉnh nguyện vọng lớp 10, làm gì để chắc chắn trúng tuyển? - 9" src="data:image/svg+xml;charset=utf-8,%3Csvg xmlns%3D''http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg'' viewBox%3D''0 0 1420 730''%3E%3Crect x=''0'' y=''0'' width=''100%'' height=''100%'' style=''fill:rgb(241, 245, 249)'' %2F%3E%3C%2Fsvg%3E" alt="Ngày cuối điều chỉnh nguyện vọng lớp 10, làm gì để chắc chắn trúng tuyển? - 9" data-width="1420" data-height="730" data-original="https://icdn.dantri.com.vn/2023/05/15/anh-chup-man-hinh-2023-05-15-luc-75809-sa-1684112564997.png?watermark=true" data-photo-id="2463927" data-watermark="true" data-track-content="" data-content-name="article-content-image" data-content-piece="article-content-image_2463927" data-content-target="/giao-duc-huong-nghiep/ngay-cuoi-dieu-chinh-nguyen-vong-lop-10-lam-gi-de-chac-chan-trung-tuyen-20230521092310023.htm" data-src="https://icdn.dantri.com.vn/thumb_w/680/2023/05/15/anh-chup-man-hinh-2023-05-15-luc-75809-sa-1684112564997.png?watermark=true" data-srcset="https://icdn.dantri.com.vn/thumb_w/680/2023/05/15/anh-chup-man-hinh-2023-05-15-luc-75809-sa-1684112564997.png?watermark=true 1x, https://icdn.dantri.com.vn/thumb_w/1020/2023/05/15/anh-chup-man-hinh-2023-05-15-luc-75809-sa-1684112564997.png?watermark=true 1.5x, https://icdn.dantri.com.vn/thumb_w/1360/2023/05/15/anh-chup-man-hinh-2023-05-15-luc-75809-sa-1684112564997.png?watermark=true 2x">
